        keyType   = CPSS_DXCH_TTI_KEY_IPV4_E;
        ruleIndex = 0;
        ruleIndex = prvWrAppDxChTcamTtiConvertedIndexGet(devNum,ruleIndex);

        st = cpssDxChTtiRuleGet(devNum, ruleIndex, keyType, &pattern, &mask, &action);
        UTF_VERIFY_EQUAL3_PARAM_MAC(GT_OK, st, devNum, ruleIndex, keyType);

        /*
            call with keyType[CPSS_DXCH_TTI_KEY_UDB_UDE_E].
        */
