        /* Call with routerTtiTcamRow [max] and valid [GT_TRUE].    */
        routerTtiTcamRow    = routerTtiTcamRowCount - 1;
        routerTtiTcamRow = prvWrAppDxChTcamTtiConvertedIndexGet(devNum,routerTtiTcamRow);

        st = cpssDxChTtiRuleSet(devNum, routerTtiTcamRow, keyType, &pattern, &mask, &action);
