    patternPtr->mac.arEther[3]      = 0xAA;
    patternPtr->mac.arEther[4]      = 0xAA;
    patternPtr->mac.arEther[5]      = 0xAA;
    patternPtr->vid                 = 0;
    patternPtr->isTagged            = GT_FALSE;
